Rules.InvariantScript:
Set(
    _today,
    Today()
);
/* if default dates are provided, set to inputs, otherwise set to today */
Set(
    _selectedStart,
    If(
        !IsBlank(Calendar.DefaultStartDate),
        Calendar.DefaultStartDate,
        _today
    )
);
Set(
    _selectedEnd,
    If(
        !IsBlank(Calendar.DefaultEndDate) && Calendar.DefaultEndDate > _selectedStart,
        Calendar.DefaultEndDate,
        Blank()
    )
);
Set(
    _firstDayOfMonth,
    DateAdd(
        _selectedStart,
        1 - Day(_selectedStart),
        TimeUnit.Days
    )
);
Set(
    _firstDayInView,
    DateAdd(
        _firstDayOfMonth,
        -(Mod(
            Weekday(_firstDayOfMonth) - 2,
            7
        ) + 1),
        TimeUnit.Days
    )
)

Set(_today,Today());
Set(_firstDayOfMonth,DateAdd(_today,1-Day(_today),TimeUnit.Days));
Set(_firstDayInView,DateAdd(_firstDayOfMonth,-(Mod(Weekday(_firstDayOfMonth)-2,7)+1),TimeUnit.Days));
Set(_selectedStart,Blank()); Set(_selectedEnd, Blank())

//Set(_today,Today());
//Set(_firstDayOfMonth,DateAdd(_today,1-Day(_today),Days));
//Set(_firstDayInView,DateAdd(_firstDayOfMonth,-(Mod(Weekday(_firstDayOfMonth)-2,7)+1),Days));
//Set(_selectedStart,Blank()); Set(_selectedEnd, Blank())

Set(
    selectedDate,
    Calendar_1.StartDate
);
Set(
    selectedEndDate,
    Calendar_1.EndDate
);
Set(
    startTime,
    DateTimeValue(
        Text(
            selectedDate,
            DateTimeFormat.ShortDate
        ) & " " & ddTimeFrom.Selected.Value
    )
);
Set(
    endTime,
    If(
        DateValue(
            Text(
                selectedEndDate,
                DateTimeFormat.ShortDate
            )
        ) < DateValue(
            Text(
                selectedDate,
                DateTimeFormat.ShortDate
            )
        ),
        DateTimeValue(
            Text(
                selectedDate,
                DateTimeFormat.ShortDate
            ) & " " & ddTimeTo.Selected.Value
        ),
        DateTimeValue(
            Text(
                selectedEndDate,
                DateTimeFormat.ShortDate
            ) & " " & ddTimeTo.Selected.Value
        )
    )
);
ClearCollect(
    col_Reservations,
    Filter(
        List_DeskReservations,
        Or(
            And(
                CheckOutFrom_DateTime >= startTime,
                Or(
                    CheckOutTo_DateTime <= endTime,
                    CheckOutFrom_DateTime < endTime
                )
            ),
            And(
                CheckOutTo_DateTime > startTime,
                Or(
                    CheckOutTo_DateTime <= endTime,
                    CheckOutFrom_DateTime < endTime
                )
            )
        )
    )
);
Switch(
    Calendar_1.SelectRange,
    true,
    If(
        false && false,
        Notify(
            "You must select a date and time to continue",
            NotificationType.Warning
        ),
        Navigate(NewBooking)
    ),
    false,
    If(
        false && false,
        Notify(
            "You must select a date and time to continue",
            NotificationType.Warning
        ),
        endTime <= startTime,
        Notify(
            "End time can't be earlier than start time. Please correct to continue.",
            NotificationType.Warning
        ),
        Navigate(NewBooking)
    )
)

